mapper.select:
SELECT COUNT(*) AS COUNT
FROM MEMBERINFO
WHERE ID = #{id} AND PWD = #{pwd}

mapper.update:
UPDATE MEMBERINFO
SET URL = #{url}, PWD = #{pwd}, NICKNAME = #{nickname}, REGION_S_ID = #{regionId1}
WHERE ID = #{id}

UPDATE MEMBER
SET CATEGORY_S_ID = #{categoryId1}
WHERE MID = (SELECT MID FROM MEMBERINFO WHERE ID = #{id})
