mapper.select:
SELECT RNUM,TITLE,NICKNAME,BDATE,CNT,B_ID,DAY,BDATE FROM ( SELECT ROWNUM AS RNUM,RECRUIT.* FROM ( SELECT CID,B_ID,CLUBMEM_ID,ID,NICKNAME,TITLE,CONTENT,POSITION_ID,POSITIONNAME,BDATE,CNT,DAY FROM RECRUIT_BOARD_VIEW WHERE CID = #{cid}
AND STATUS NOT IN('마감','투표중')
AND STATUS = '투표중'
ORDER BY TO_NUMBER(B_ID) DESC )RECRUIT ) WHERE RNUM >= #{start} AND RNUM <= #{end}

SELECT POSITION_ID
FROM CLUBMEMBERLIST_VIEW
WHERE ID = #{id} AND CID = #{cid}

SELECT CLUBMEM_ID
FROM CLUBMEMBERLIST_VIEW
WHERE ID = #{id} AND CID = #{cid}

mapper.update:
UPDATE RECRUIT_BOARD
SET CNT = CNT + 1
WHERE B_ID = #{b_id}

 UPDATE RECRUIT_BOARD
SET TITLE = #{title} , CONTENT = #{content}
WHERE B_ID = #{b_id}

mapper.delete:
DELETE
FROM VOLUNTEER
WHERE B_ID = #{b_id} AND CLUBMEM_ID = #{clubmem_id}

